            # self.WeMoAppLog = Logger(os.path.basename("IOSWeMoApp_Generic.py").replace(".py","_%s_"%smartPhoneDeviceName.split(":")[-1].strip()+strftime("%Y-%m-%d_%H-%M-%S")+".log"))
